/* ===== RESET & BASE ===== */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Segoe UI", sans-serif;
}

body {
  color: #222;
  background: #f9f9f9;
  line-height: 1.7;
}

.container {
  width: 90%;
  max-width: 1100px;
  margin: auto;
}

/* ===== Top Bar ===== */
.topbar {
  background-color: #007a00;
  color: white;
  font-size: 0.9rem;
  padding: 6px 0;
}

.topbar .container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.topbar-right span {
  margin-left: 15px;
}

/* ===== Header Section ===== */
#main-header {
  background-color: #fff;
  padding: 10px 0;
  border-bottom: 2px solid #d94f04;
}

.header-flex {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
}

.header-left {
  display: flex;
  align-items: center;
}

.logo-img {
  height: 80px;
  width: auto;
  margin-right: 15px;
}

.header-text h3 {
  font-size: 0.8rem;
  color: #777;
  margin: 0;
}

.header-text h1 {
  color: #d94f04;
  font-size: 1.4rem;
  font-weight: bold;
  margin: 3px 0;
}

.header-text p {
  margin: 0;
  color: #333;
  font-size: 0.9rem;
}

.header-right {
  text-align: right;
}

.office-hours {
  font-size: 0.9rem;
  margin-bottom: 5px;
}

.language-select select {
  padding: 5px;
  border-radius: 4px;
  border: 1px solid #ccc;
}

/* ===== Navigation Bar ===== */
#navbar {
  background-color: #b23b00;
}

.nav-container {
  display: flex;
  justify-content: center;
  align-items: center;
}

.nav-menu {
  list-style: none;
  display: flex;
  margin: 0;
  padding: 0;
}

.nav-menu li {
  position: relative;
}

.nav-menu a {
  display: block;
  color: white;
  padding: 15px 20px;
  text-decoration: none;
  font-weight: bold;
}

.nav-menu a:hover,
.nav-menu a.active {
  background-color: #007a00;
  color: #fff;
}

/* Dropdown */
.dropdown-content {
  display: none;
  position: absolute;
  background-color: #007a00;
  min-width: 200px;
  z-index: 999;
}

.dropdown-content li a {
  color: white;
  padding: 10px;
  display: block;
}

.dropdown:hover .dropdown-content {
  display: block;
}

/* ===== HEADER / NAVBAR ===== */
header {
  background: #b5431a;
  color: #fff;
  position: sticky;
  top: 0;
  z-index: 1000;
  transition: all 0.3s ease-in-out;
}

header.scrolled {
  background: #8b3213;
  box-shadow: 0 3px 8px rgba(0, 0, 0, 0.2);
}

.nav-container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 10px 40px;
}

.logo-img {
  height: 50px;
  margin-right: 10px;
  vertical-align: middle;
}

/* ===== NAV MENU ===== */
#nav-menu ul {
  display: flex;
  list-style: none;
  gap: 20px;
  flex-wrap: nowrap;
  white-space: nowrap;
}

#nav-menu {
  flex: 1;
  display: flex;
  justify-content: flex-end;
  overflow-x: auto;
}

#nav-menu a {
  color: #fff;
  text-decoration: none;
  font-weight: bold;
  transition: 0.3s;
  display: inline-block;
}

#nav-menu a:hover {
  color: #ffe600;
}

#nav-toggle {
  display: none;
  background: none;
  color: #fff;
  font-size: 1.8rem;
  border: none;
  cursor: pointer;
}

/* ===== DROPDOWN MENU ===== */
.dropdown {
  position: relative;
}

.dropdown .arrow {
  font-size: 0.6em;
  margin-left: 4px;
}

.dropdown-content {
  display: none;
  position: absolute;
  background-color: #fff;
  min-width: 200px;
  box-shadow: 0 8px 16px rgba(0,0,0,0.15);
  z-index: 999;
  border-radius: 6px;
  overflow: hidden;
}

.dropdown-content li {
  list-style: none;
}

.dropdown-content a {
  color: #333;
  padding: 12px 16px;
  text-decoration: none;
  display: block;
  transition: background 0.3s;
}

.dropdown-content a:hover {
  background-color: #f0f0f0;
  color: #007b5e;
}

/* Show dropdown on hover (desktop) */
.dropdown:hover .dropdown-content {
  display: block;
}

/* For mobile (click to toggle) */
.dropdown.active .dropdown-content {
  display: block;
}

/* Responsive Dropdown Adjustments */
@media (max-width: 768px) {
  .dropdown-content {
    position: static;
    box-shadow: none;
    background: #f8f8f8;
    border-radius: 0;
  }
  .dropdown-content a {
    padding-left: 30px;
  }
}

/* ===== HERO SLIDER ===== */
.hero-slider {
  position: relative;
  height: 80vh;
  overflow: hidden;
}
.slide {
  position: absolute;
  inset: 0;
  background-size: cover;
  background-position: center;
  opacity: 0;
  transition: opacity 1s ease-in-out;
}
.slide.active {
  opacity: 1;
}
.slide .overlay {
  position: absolute;
  inset: 0;
  background: rgba(0, 0, 0, 0.4);
}
.hero-text {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: white;
}
.hero-text h1 {
  font-size: 2.5rem;
  font-weight: bold;
}
.hero-text p {
  margin-top: 10px;
  font-size: 1rem;
}

/* ===== BUTTONS ===== */
.btn {
  background: #b5431a;
  color: #fff;
  padding: 10px 20px;
  border-radius: 5px;
  text-decoration: none;
  transition: 0.3s;
  display: inline-block;
}
.btn:hover {
  background: #008000;
}

/* ===== CHAIRMAN SECTION ===== */
.chairman {
  background: #0a3161;
  color: white;
  padding: 60px 20px;
}
.chairman-flex {
  display: flex;
  align-items: center;
  justify-content: space-around;
  flex-wrap: wrap;
  gap: 40px;
}

/* --- Chairman Text Styling --- */
.chairman-text {
  max-width: 600px;
}
.chairman-text h2 {
  font-size: 2rem;
  color: #0a3161;
  margin-bottom: 20px;
}
.chairman-text p {
  margin-bottom: 15px;
  line-height: 1.7;
  text-align: justify;
  color: #222;
}

/* --- Chairman Photo Styling --- */
.chairman-photo {
  text-align: center;
}
.chairman-photo img {
  width: 280px;
  border-radius: 10px;
  box-shadow: 0 4px 10px rgba(0,0,0,0.2);
}
.chairman-photo h4 {
  margin-top: 10px;
  color: #b5431a;
  font-weight: bold;
}

/* ===== AGENDA SECTION ===== */
.agenda {
  position: relative;
  background: linear-gradient(135deg, #007a00 0%, #005500 100%);
  height: 80vh;
  display: flex;
  align-items: center;
  justify-content: center;
}
.agenda-overlay {
  background: rgba(10, 49, 97, 0.9);
  padding: 60px;
  color: white;
  text-align: center;
  width: 100%;
}
.agenda-text h2 {
  font-size: 2rem;
  margin-bottom: 10px;
}
.agenda-text h3 {
  font-size: 1.3rem;
  margin-bottom: 15px;
}

/* ===== SERVICES / DEPARTMENTS ===== */
section {
  padding: 60px 0;
}

.grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
}

.card {
  background: #fff;
  border-radius: 8px;
  padding: 20px;
  border-top: 4px solid #008000;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s, border-color 0.3s;
  text-align: center;
}
.card:hover {
  transform: translateY(-5px);
  border-top-color: #b5431a;
}
.card.green { background: #e8f8e8; }
.card.gold { background: #fff9e6; }
.card.red { background: #fdeaea; }

/* ===== NEWS ===== */
.news {
  background: #fff5f3;
  padding: 60px 20px;
  text-align: center;
}
.news h2 {
  font-size: 2rem;
  margin-bottom: 10px;
  color: #0a3161;
}
.news-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
  margin-top: 40px;
}
.news-item {
  background: #fff;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 3px 8px rgba(0, 0, 0, 0.1);
}
.news-item img {
  width: 100%;
  height: 180px;
  object-fit: cover;
}
.news-item h3 {
  color: #0a3161;
  padding: 10px;
  font-size: 1.1rem;
}
.news-item p {
  padding: 0 10px 15px;
}

/* ===== CONTACT FORM ===== */
form {
  display: flex;
  flex-direction: column;
  gap: 15px;
  max-width: 500px;
  margin: auto;
}
form input,
form textarea {
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
}
form button {
  border: none;
  cursor: pointer;
  background: #b5431a;
  color: #fff;
  padding: 10px 20px;
  border-radius: 4px;
  transition: 0.3s;
}
form button:hover {
  background: #008000;
}

/* ===== FOOTER ===== */
footer {
  background: #0a3161;
  color: #fff;
  text-align: center;
  padding: 15px;
  font-size: 0.9rem;
}

/* ===== SCROLL ANIMATION ===== */
.fade-in {
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s ease-out;
}
.fade-in.visible {
  opacity: 1;
  transform: translateY(0);
}

/* ===== RESPONSIVE ===== */
@media (max-width: 768px) {
  #nav-menu {
    display: none;
    position: absolute;
    background: #b5431a;
    top: 60px;
    right: 0;
    width: 100%;
    text-align: center;
    padding: 20px 0;
  }
  #nav-menu ul {
    flex-direction: column;
    gap: 15px;
  }
  #nav-menu.show {
    display: block;
  }
  #nav-toggle {
    display: block;
  }
  .hero-text h1 {
    font-size: 1.8rem;
  }
  .agenda {
    height: auto;
    padding: 60px 20px;
  }
  .chairman-flex {
    flex-direction: column;
  }
}

/* ===== CONTACT US (UPDATED) ===== */
/* ===== CONTACT US (3-COLUMN LAYOUT) ===== */
#contact {
  background: #000;
  color: #fff;
  padding: 30px 300px;
}

#contact h2 {
  text-align: center;
  font-size: 2rem;
  margin-bottom: 50px;
  color: #ffe600;
}

#contact .contact-grid {
  display: grid;
  grid-template-columns: 1.1fr 1fr 1.3fr; /* left - middle - right */
  gap: 40px;
  align-items: flex-start;
}

/* ===== Column Wrappers ===== */
.contact-left,
.contact-center,
.contact-right {
  display: flex;
  flex-direction: column;
  gap: 25px;
}

/* ===== Subsection Titles ===== */
#contact h3 {
  color: #ffe600;
  margin-bottom: 10px;
  font-size: 1.2rem;
  border-left: 3px solid #ffe600;
  padding-left: 10px;
}

/* ===== Text & Links ===== */
#contact p {
  color: #ccc;
  line-height: 1.7;
}

#contact ul {
  list-style: none;
  padding: 0;
}

#contact ul li {
  margin-bottom: 8px;
  color: #bbb;
  font-size: 0.95rem;
}

#contact a {
  color: #ffe600;
  text-decoration: none;
  transition: color 0.3s ease;
}

#contact a:hover {
  color: #fff;
}

/* ===== Follow Us Icons ===== */
.follow-us {
  display: flex;
  gap: 12px;
}

.follow-us a {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 38px;
  height: 38px;
  border-radius: 50%;
  background: #222;
  color: #ffe600;
  font-size: 1.1rem;
  transition: all 0.3s ease;
}

.follow-us a:hover {
  background: #ffe600;
  color: #000;
}

/* ===== Contact Form (Right Column) ===== */
.contact-form {
  background: #111;
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0 3px 8px rgba(0, 0, 0, 0.3);
}

.contact-form form {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.contact-form input,
.contact-form textarea {
  width: 100%;
  padding: 12px;
  border: none;
  border-radius: 4px;
  background: #222;
  color: #fff;
}

.contact-form input::placeholder,
.contact-form textarea::placeholder {
  color: #aaa;
}

.contact-form textarea {
  min-height: 120px;
  resize: vertical;
}

.contact-form button {
  background: #ffe600;
  color: #000;
  font-weight: bold;
  border: none;
  padding: 12px;
  border-radius: 4px;
  cursor: pointer;
  transition: background 0.3s ease;
}

.contact-form button:hover {
  background: #b5431a;
  color: #fff;
}

/* ===== Responsive Adjustments ===== */
@media (max-width: 992px) {
  #contact .contact-grid {
    grid-template-columns: 1fr 1fr;
  }
}

@media (max-width: 768px) {
  #contact {
    padding: 60px 20px;
  }

  #contact .contact-grid {
    grid-template-columns: 1fr;
    gap: 40px;
  }
}
/* Smooth scroll for in-page navigation */
html {
  scroll-behavior: smooth;
}

/* Council Personnel links styling */
.council-personnel ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.council-personnel li {
  margin: 10px 0;
}

.council-link {
  color: #ffffff; /* white text */
  text-decoration: none;
  font-weight: 500;
  position: relative;
  transition: color 0.3s ease;
}

/* Gold underline hover effect */
.council-link::after {
  content: "";
  position: absolute;
  left: 0;
  bottom: -3px;
  width: 0%;
  height: 2px;
  background-color: #ffd700; /* gold */
  transition: width 0.3s ease;
}

/* Hover and active state */
.council-link:hover,
.council-link:focus {
  color: #ffd700; /* gold */
}

.council-link:hover::after,
.council-link:focus::after {
  width: 100%;
}

/* Active state (optional: adds glow when the section is in view) */
.council-link.active {
  color: #ffd700;
  text-shadow: 0 0 8px rgba(255, 215, 0, 0.7);
}
/* ===== Projects Section ===== */
.projects {
  padding: 60px 0;
  text-align: center;
  background-color: #fff;
}

.projects h2 {
  color: #0a8300;
  font-size: 2.2rem;
  margin-bottom: 10px;
}

.project-intro {
  color: #b34700;
  font-weight: 500;
  margin-bottom: 40px;
}

.project-gallery {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
}

.project-item {
  position: relative;
  overflow: hidden;
  border-radius: 8px;
}

.project-item img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  border-radius: 8px;
}

.project-item .date {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 8px 0;
  font-size: 0.9rem;
  background: linear-gradient(to top, rgba(0, 0, 0, 0.6), transparent);
  color: #fff;
}

